package variablesAndDatatypes;

public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//integer
		int age=30;
		System.out.println(age);
		
		
		//Character
		
		char geneder='M';
		
		//Decimal values
		
		double marks=8.2;
		float grade=8.9f;
		
		//Boolean values
		boolean status=true;
		
		//strings
		
		String name="Pranali";
		
		//for integer values
		
		byte a=10;
		short b=20;
		int c=30;
		long d=40;
		
		
		
	}

}
