package variablesAndDatatypes;

public class NestedIfStatments {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=20;
		int b=30;
		int c=40;
		
		//login
		if(a>b)
		{
			//compose
			if(a>c)
			
			{
			System.out.println("a is greatest");
		}
			//Sent
			if(b>c)
			{
				//inbox
				if(c>a)
				
				{
				System.out.println("a is greatest");
			}

	}
		}
	
		else {
			System.out.println("C is greatest");
		}
		
	}
}
